import { Lab } from '@antv/g2plot';

// Step 1: å£°æ˜æ•°æ®æº
// G2Plot å¯¹æ•°æ®æºæ ¼å¼çš„è¦æ±‚ï¼Œä»…ä»…æ˜¯ JSON æ•°ç»„ï¼Œæ•°ç»„çš„æ¯ä¸ªå…ƒç´ æ˜¯ä¸€ä¸ªæ ‡å‡† JSON å¯¹è±¡ã€‚
const data = [
  { area: 'Central', value: 0.218 },
  { area: 'East', value: 0.295 },
  { area: 'South', value: 0.171 },
  { area: 'West', value: 0.316 },
];

const defaultGrey = '#BFBFBF';
const semanticGreen = '#30BF78'; /** è¯­ä¹‰ç»¿ */

// Step 2: åˆ›å»ºå›¾è¡¨
const labPlot = new Lab.MultiView('container', {
  appendPadding: 8,
  tooltip: { showMarkers: false },
  views: [
    {
      region: { start: { x: 0, y: 0 }, end: { x: 1 / 2, y: 2 / 5 } },
      data, // å›¾è¡¨æ•°æ®
      meta: {
        value: {
          alias: 'é”€å”®é¢(ä¸‡)',
        },
      },
      axes: { area: { tickLine: false }, value: false },
      coordinate: { cfg: { isTransposed: true } },
      geometries: [
        {
          type: 'interval',
          xField: 'area',
          yField: 'value',
          mapping: {
            color: ({ area }) => {
              const value = data.find((d) => d.area === area).value;
              return value > 0.3 ? semanticGreen : defaultGrey;
            },
            style: { lineWidth: 1 },
          },
          label: {
            position: 'left',
            formatter: ({ value }) => `${(value * 100).toFixed(1)}%`,
            style: { fill: '#fff' },
          },
        },
      ],
    },
