import { Funnel } from '@antv/g2plot';

const originData = [
  { stage: 'è§¦è¾¾æ¬¡æ•°', number: 789 },
  { stage: 'å“åº”æ¬¡æ•°', number: 453 },
  { stage: 'åˆ†äº«æ¬¡æ•°', number: 193 },
];

const data = originData.map((item, index, arr) => {
  if (index === 0) return item;
  return {
    ...item,
    unConversation: arr[index - 1].number - item.number,
    conversionPec: item.number / arr[index - 1].number,
    unConversionPec: 1 - item.number / arr[index - 1].number,
  };
});

const containerStyle = 'margin: 16px -8px; display: flex;';
const boxStyle = 'padding: 0px 16px;';
const titleStyle = 'font-weight: bold;';
const tooltipItemStyle = 'margin-top: 12px; display: flex; width: 120px; justify-content: space-between;';

const funnelPlot = new Funnel('container', {
  data: data,
  xField: 'stage',
  yField: 'number',
  legend: false,
  conversionTag: false,
  tooltip: {
    follow: true,
    enterable: true,
    offset: 5,
    customContent: (value, items) => {
      if (!items || items.length <= 0) return;
      const { data: itemData } = items[0];

      if (itemData.stage === 'è§¦è¾¾æ¬¡æ•°') {
        return `<div style='${tooltipItemStyle} margin-bottom: 12px; padding: 0 8px;'><span>å¾…è½¬åŒ–äººæ•°</span><span>${itemData.number}</span></div>`;
      }

      return (
        `<div style='${containerStyle}'>` +
        `<div style='${boxStyle} border-right: 1px solid #c2c2c2'>` +
        `<div style='${titleStyle}'>è½¬åŒ–</div>` +
        `<div style='${tooltipItemStyle}'><span>è½¬åŒ–äººæ•°</span><span>${itemData.number}</span></div>` +
        `<div style='${tooltipItemStyle}'><span>è½¬åŒ–ç‡</span><span>${(itemData.conversionPec * 100).toFixed(
          0
        )}%</span></div>` +
        `</div>` +
        `<div style='${boxStyle}'>` +
        `<div style='${titleStyle}'>æœªè½¬åŒ–</div>` +
        `<div style='${tooltipItemStyle}'><span>æœªè½¬åŒ–äººæ•°</span><span>${itemData.unConversation}</span></div>` +
        `<div style='${tooltipItemStyle}'><span>æœªè½¬åŒ–ç‡</span><span>${(itemData.unConversionPec * 100).toFixed(
          0
        )}%</span></div>` +
        `</div>` +
        `</div>`
      );
    },
  },
});

funnelPlot.render();

