      geometry: 'line',
      smooth: true,
      color: '#5AD8A6',
      lineStyle: {
        lineWidth: 4,
        opacity: 0.5,
      },
      label: {},
      point: {
        shape: 'circle',
        size: 4,
        style: {
          opacity: 0.5,
          stroke: '#5AD8A6',
          fill: '#fff',
        },
      },
    },
  ],
});

dualAxes.render();

