import { P, G2 } from '@antv/g2plot';

// è‡ªå®šä¹‰å›¾å½¢
G2.registerShape('interval', 'hill', {
  draw(info, container) {
    const { points, style } = info;

    let path = [
      ['M', points[0].x, points[0].y],
      ['L', (points[1].x + points[2].x) / 2, points[1].y],
      ['L', points[3].x, points[3].y],
      ['Z'],
    ];

    path = this.parsePath(path);

    return container.addShape('path', {
      attrs: {
        path,
        ...style,
      },
    });
  },
});

// 1. å®šä¹‰é…ç½®
const defaultOptions = {
  columnWidthRatio: 1.2,
};

// 2. adaptor å®ç°
function adaptor(params) {
  const { chart, options } = params;
  const { data, xField, yField, columnWidthRatio, columnStyle, seriesField } = options;

  // æ•°æ®
  chart.data(data);

  // å‡ ä½•å›¾å½¢
  const i = chart
    .interval()
    .position(`${xField}*${yField}`)
    .shape('hill')
    .style(`${xField}*${yField}`, (x, y) => {
      return typeof columnStyle === 'function' ? columnStyle({ [xField]: x, [yField]: y }) : columnStyle;
    });

  // è®¾ç½®é‡å æ¯”ç‡
  chart.theme({
    columnWidthRatio: columnWidthRatio,
  });

  const gap = (1 / data.length / 2) * columnWidthRatio; // å·¦å³é¢„ç•™
  chart.scale({
    genre: {
      range: [gap, 1 - gap],
    },
  });

  return params;
}

// 3. G2Plot ä¸Šä½¿ç”¨
