      genre: {
        alias: 'æ¸¸æˆç§ç±»', // åˆ—å®šä¹‰ï¼Œå®šä¹‰è¯¥å±æ€§æ˜¾ç¤ºçš„åˆ«å
      },
      sold: {
        alias: 'é”€å”®é‡',
      },
    },
    xField: 'genre',
    yField: 'sold',
    columnStyle: {
      fillOpacity: 0.3,
      fill: 'red',
    },
  },
  adaptor,
  defaultOptions
); // å¼•å…¥ä¸Šè¿°çš„å°è£…ï¼Œæˆ–è€…é™ä¸Šè¿°ä»£ç å‘åŒ…

hill.render();

